mapper.select:
select

from sctrans.T_TS_AWARD_BASE
where case_code = #{caseCode,jdbcType=VARCHAR}
and srv_code = #{srvCode,jdbcType=VARCHAR}
and IS_DELETED='0'

select count(distinct yd.TEAM_ID) from sctrans.t_yucui_org_hierarchy yd
left join sctrans.V_USER_ORG_JOB uoj
on yd.ORG_ID= uoj.ORG_ID and uoj.JOB_CODE in
('Senior_Manager','Manager') and uoj.USER_ID=#{0}
left join sctrans.V_USER_ORG_JOB uoj1
on yd.DISTRICT_ID= uoj1.ORG_ID and uoj1.JOB_CODE ='director' and
uoj1.USER_ID=#{0}
left join sctrans.V_USER_ORG_JOB uoj2
on yd.hq_id= uoj2.ORG_ID and uoj2.JOB_CODE ='GeneralManager' and
uoj2.USER_ID=#{0}
where (uoj.ORG_ID is not null or uoj1.ORG_ID is not null or uoj2.ORG_ID is
not null) and yd.TEAM_ID is not null;
;
