mapper.select:
select 

from sctrans.T_TS_KPI_PSN_MONTH  with(nolock)
where PKID = #{pkid,jdbcType=BIGINT}

    select 
    
    from sctrans.T_TS_KPI_PSN_MONTH  with(nolock)
    
		
			PARTICIPANT = #{participant}
		
		
			
			AND DATEDIFF(mm,BELONG_MONTH,#{belongMonth}) = 0

mapper.delete:
    delete from sctrans.T_TS_KPI_PSN_MONTH
   
		
			PARTICIPANT = #{participant}
		
		
			AND DATEDIFF(mm,BELONG_MONTH,#{belongMonth}) = 0
