mapper.update:
UPDATE TC SET TC.GRP_CODE = SOH.GROUP_CODE, TC.GRP_NAME = SOH.GROUP_NAME, TC.AR_CODE = SOH.BUSIAR_CODE, TC.AR_NAME = SOH.BUSIAR_NAME, TC.QYJL_NAME = SOH.JQYJL_MGR, TC.QYZJ_NAME = SOH.JQYZJ_MGR, TC.SWZ_CODE = SOH.BUSISWZ_CODE, TC.SWZ_NAME = SOH.BUSISWZ_NAME, TC.WZ_CODE= SOH.BUSIWZ_CODE, TC.WZ_NAME = SOH.BUSIWZ_NAME, TC.QJDS_NAME = SOH.JQYDS_MGR, TC.BA_CODE = SOH.BUSIBA_CODE, TC.BA_NAME = SOH.BUSIBA_NAME, TC.AGENT_USERNAME = U.USERNAME, TC.CREATE_TIME = GETDATE(), TC.UPDATE_TIME = GETDATE(), TC.CREATE_BY = @{SESSION_USER_ID}, TC.UPDATE_BY = @{SESSION_USER_ID}
FROM sctrans.T_TO_CASE_INFO TC LEFT JOIN sctrans.SYS_USER U ON TC.AGENT_CODE = U.ID LEFT JOIN sctrans.V_SYS_ORG_HIERARCHY SOH ON U.ORG_ID = SOH.ORG_ID
WHERE SOH.ORG_ID = #{orgId} AND TC.CASE_CODE = #{caseCode}

mapper.select:
	SELECT 
   	COUNT(CASE_CODE) AS countJDS
   	FROM sctrans.T_TO_CASE WHERE 
		 datediff(month,sctrans.T_TO_CASE.CREATE_TIME,getdate())=0
		AND  CASE_CODE IN(
		(
			select CASE_CODE from sctrans.T_TO_CASE
			WHERE 1=1			
			AND LEADING_PROCESS_ID = #{userId,jdbcType=VARCHAR}
				)UNION(
				SELECT CASE_CODE FROM sctrans.T_TG_SERV_ITEM_AND_PROCESSOR
				WHERE 1=1
				AND PROCESSOR_ID = #{userId,jdbcType=VARCHAR}				  	
			)

		)

mapper.update:
update sctrans.T_TO_CASE_INFO 
	set target_code = #{targetCode} 
where ctm_code = #{ctmCode}
