mapper.select:
select 

from sctrans.T_TO_CASE with(nolock)
where PKID = #{pkid,jdbcType=BIGINT}

mapper.update:
update sctrans.T_TO_CASE set CASE_CODE = #{caseCode,jdbcType=VARCHAR}, CTM_CODE = #{ctmCode,jdbcType=VARCHAR}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, STATUS = #{status,jdbcType=NVARCHAR}, CASE_PROPERTY = #{caseProperty,jdbcType=NVARCHAR}, LEADING_PROCESS_ID = #{leadingProcessId,jdbcType=VARCHAR}, ORG_ID = #{orgId,jdbcType=VARCHAR},
DISTRICT_ID = #{districtId,jdbcType=VARCHAR},
CLOSE_TIME = #{closeTime,jdbcType=TIMESTAMP}, UPDATE_TIME = GETDATE(), UPDATE_BY = @{SESSION_USER_ID} where PKID = #{pkid,jdbcType=BIGINT}

mapper.select:
select 

from sctrans.T_TO_CASE  with(nolock)
where CASE_CODE = #{caseCode,jdbcType=VARCHAR}

    select 

    from sctrans.T_TO_CASE c with(nolock) where not exists(select 1 from sctrans.T_CS_CASE_SATISFACTION ccs where ccs.CASE_CODE = c.CASE_CODE)
    and exists(
		select 1 from sctrans.T_HI_WORKFLOW w with(nolock) where w.CASE_CODE = c.CASE_CODE and w.STATUS in (0,4)
		and exists(
			select 1 from sctrans.ACT_HI_TASKINST aht with(nolock) where aht.PROC_INST_ID_ = w.INST_CODE and aht.TASK_DEF_KEY_='TransSign'
		)
	)
	and not exists(
		select 1 from sctrans.T_TS_KPI_SRV_CASE ks where ks.CASE_CODE = c.CASE_CODE)
	)
	order by c.CASE_CODE asc

  SELECT 
  	COUNT(CASE_CODE) AS countJDS
  	FROM sctrans.T_TO_CASE with(nolock)  WHERE 
	convert(char(7) ,CREATE_TIME , 120) = #{time,jdbcType=VARCHAR} 
	AND LEADING_PROCESS_ID = #{leadingProcessId,jdbcType=VARCHAR}

	SELECT 
		COUNT(CASE_CODE) AS countJDS
	FROM sctrans.T_TO_CASE  with(nolock)
	WHERE datediff(month,CREATE_TIME,getdate())=0 


      AND   CREATE_TIME >= #{startDate,jdbcType=VARCHAR}   


      AND  CREATE_TIME  <= #{endDate,jdbcType=VARCHAR}  



     AND  CASE_CODE IN(
	(
		select CASE_CODE from sctrans.T_TO_CASE
		WHERE 1=1			
		AND ORG_ID = #{orgId,jdbcType=VARCHAR}
			)UNION(
			SELECT CASE_CODE FROM sctrans.T_TG_SERV_ITEM_AND_PROCESSOR
			WHERE 1=1
			AND  ORG_ID = #{orgId,jdbcType=VARCHAR}				  	
		)

	)

SELECT a.createTime, SUM (countJDS) AS countJDS FROM ( SELECT CONVERT (CHAR(7), CREATE_TIME, 120) AS createTime, COUNT (CASE_CODE) AS countJDS FROM sctrans.T_TO_CASE WHERE datediff(YEAR, CREATE_TIME, getdate()) = 0 AND CASE_CODE IN ( ( SELECT CASE_CODE FROM sctrans.T_TO_CASE WHERE 1 = 1
AND ORG_ID = #{orgId,jdbcType=VARCHAR}
) UNION ( SELECT CASE_CODE FROM sctrans.T_TG_SERV_ITEM_AND_PROCESSOR WHERE 1 = 1
AND ORG_ID = #{orgId,jdbcType=VARCHAR}
) ) GROUP BY CONVERT (CHAR(7), CREATE_TIME, 120) UNION ALL SELECT CONVERT (CHAR(7), CREATE_TIME, 120) AS createTime, COUNT (CASE_CODE) AS countJDS FROM sctrans.T_TO_CASE WHERE datediff(MONTH, Close_Time, getdate()) = 0 AND CASE_CODE IN ( SELECT CASE_CODE FROM sctrans.T_TG_SERV_ITEM_AND_PROCESSOR WHERE 1 = 1
AND ORG_ID = #{orgId,jdbcType=VARCHAR}
) GROUP BY CONVERT (CHAR(7), CREATE_TIME, 120) ) AS a GROUP BY a.createTime

  SELECT 
  	COUNT(CASE_CODE) AS countJDS
  	FROM sctrans.T_TO_CASE WHERE 
	convert(char(7) ,CREATE_TIME , 120) = #{time,jdbcType=VARCHAR} 


		AND  CASE_CODE IN(
	(
		select CASE_CODE from sctrans.T_TO_CASE
		WHERE 1=1			
		AND ORG_ID = #{orgId,jdbcType=VARCHAR}
			)UNION(
			SELECT CASE_CODE FROM sctrans.T_TG_SERV_ITEM_AND_PROCESSOR
			WHERE 1=1
			AND  ORG_ID = #{orgId,jdbcType=VARCHAR}				  	
		)

	)

  SELECT 
		COUNT(CASE_CODE) AS countJDS
	FROM sctrans.T_TO_CASE 
	WHERE datediff(year,CREATE_TIME,getdate())=0 
     AND  CASE_CODE IN(
	(
		select CASE_CODE from sctrans.T_TO_CASE
		WHERE 1=1			
		AND ORG_ID IN 
		#{item1}

	      AND   CREATE_TIME >= #{param2,jdbcType=VARCHAR}   


	      AND  CREATE_TIME  <= #{param3,jdbcType=VARCHAR}  

			)UNION(
			SELECT CASE_CODE FROM sctrans.T_TG_SERV_ITEM_AND_PROCESSOR
			WHERE 1=1
			AND  ORG_ID IN 
		#{item1}

	      AND   CREATE_TIME >= #{param2,jdbcType=VARCHAR}   


	      AND  CREATE_TIME  <= #{param3,jdbcType=VARCHAR}  

