SELECT COUNT(DISTINCT CASE_CODE) as countJDS FROM sctrans.T_TO_CASE WHERE CASE_CODE IN ( (SELECT DISTINCT c.CASE_CODE FROM sctrans.T_TO_CASE c INNER JOIN sctrans.SYS_ORG o ON c.ORG_ID=o.ID WHERE datediff(month,CREATE_TIME,getdate())=0
AND o.PARENT_ID= #{orgId,jdbcType=VARCHAR}
) UNION (SELECT DISTINCT s.case_code FROM sctrans.T_TO_CASE c LEFT JOIN sctrans.SYS_ORG o
ON o.PARENT_ID=#{orgId,jdbcType=VARCHAR}
LEFT JOIN sctrans.T_TG_SERV_ITEM_AND_PROCESSOR s ON s.ORG_ID=o.ID WHERE datediff(month,CREATE_TIME,getdate())=0 ) )
AND convert(char(7) ,CREATE_TIME , 120) = #{time,jdbcType=VARCHAR}

SELECT COUNT(CASE_CODE) FROM ( SELECT s.case_code FROM ( SELECT [CASE_CODE], COUNT (CASE_CODE) as redcount FROM [sctrans].[T_TO_TRANS_PLAN] WHERE RED_LOCK = 1 GROUP BY CASE_CODE ) AS s WHERE 1=1
AND redcount >= #{param2,jdbcType=BIGINT}
AND redcount <= #{param3,jdbcType=BIGINT}
) AS temp WHERE CASE_CODE IN ( SELECT CASE_CODE from sctrans.T_TG_SERV_ITEM_AND_PROCESSOR WHERE ORG_ID IN #{item1} )

SELECT COUNT(CASE_CODE) FROM ( SELECT s.case_code FROM ( SELECT [CASE_CODE], COUNT (CASE_CODE) as redcount FROM [sctrans].[T_TO_TRANS_PLAN] WHERE RED_LOCK = 1 GROUP BY CASE_CODE ) AS s WHERE 1=1
AND redcount >= #{param2,jdbcType=BIGINT}
AND redcount <= #{param3,jdbcType=BIGINT}
) AS temp WHERE CASE_CODE IN ( SELECT CASE_CODE from sctrans.T_TG_SERV_ITEM_AND_PROCESSOR WHERE PROCESSOR_ID IN #{item1} )
