SELECT COUNT(CASE_CODE) AS countJAS FROM sctrans.T_TO_CASE where datediff(month,Close_Time,getdate())=0 AND CASE_CODE IN ( (SELECT DISTINCT c.CASE_CODE FROM sctrans.T_TO_CASE c INNER JOIN sctrans.SYS_ORG o ON c.ORG_ID=o.ID WHERE datediff(month,CREATE_TIME,getdate())=0
AND o.PARENT_ID= #{orgId,jdbcType=VARCHAR}
) UNION (SELECT DISTINCT s.case_code FROM sctrans.T_TO_CASE c LEFT JOIN sctrans.SYS_ORG o
ON o.PARENT_ID=#{orgId,jdbcType=VARCHAR}
LEFT JOIN sctrans.T_TG_SERV_ITEM_AND_PROCESSOR s ON s.ORG_ID=o.ID WHERE datediff(month,CREATE_TIME,getdate())=0 ) )
AND convert(char(7) ,CREATE_TIME , 120) = #{time,jdbcType=VARCHAR}
