mapper.select:
select CODE,NAME from sctrans.sys_dict where TYPE = #{dictType}

	AND
	
		CODE = #{item.CODE}

SELECT B.CASE_CODE AS CODE, CASE WHEN LEN(StuList)=0 THEN '' ELSE LEFT(StuList,LEN(StuList)-1) END NAME
FROM ( SELECT CASE_CODE, (SELECT GUEST_NAME+'/' FROM sctrans.T_TG_GUEST_INFO WHERE CASE_CODE=A.CASE_CODE AND TRANS_POSITION = A.TRANS_POSITION FOR XML PATH('')) AS StuList FROM sctrans.T_TG_GUEST_INFO A WHERE A.TRANS_POSITION = #{dictType}
AND
A.CASE_CODE = #{item.CASE_CODE}
GROUP BY A.CASE_CODE,A.TRANS_POSITION ) B

SELECT B.CASE_CODE AS CODE, CASE WHEN LEN(StuList)=0 THEN '' ELSE LEFT(StuList,LEN(StuList)-1) END NAME FROM ( SELECT CASE_CODE, (SELECT GUEST_PHONE+'/' FROM sctrans.T_TG_GUEST_INFO WHERE CASE_CODE=A.CASE_CODE AND TRANS_POSITION = A.TRANS_POSITION FOR XML PATH('')) AS StuList FROM sctrans.T_TG_GUEST_INFO A WHERE A.TRANS_POSITION = #{dictType}
AND
A.CASE_CODE = #{item.CASE_CODE}
GROUP BY A.CASE_CODE,A.TRANS_POSITION ) B
