mapper.select:
SELECT pri.PKID, pri.EVA_CODE, pri.ARISER_ID, pri.EVA_TYPE, pri.LOAN_BANK, pri.ORIGIN_PRICE, pri.TOTAL_PRICE, pri.EVAL_TIME, pro.RESIDENCE_NAME, pro.FLOOR, pro.TOTAL_FLOOR, pro.AREA, pro.COMPLETE_YEAR, pro.PROP_TYPE, pro.ROOM, pro.HALL, pro.TOILET, pro.HOUSE_AGE, fin.FIN_ORG_NAME EVA_COMPANY, cas.CASE_CODE, cas.CCAI_CODE, si.CON_PRICE, pri.STATUS, ART.ID_ taskId, pri.REASON FROM sctrans.T_TO_EVA_PRICING pri INNER JOIN sctrans.T_TO_EGU_PROPERTY_INFO pro ON pri.EVA_CODE = pro.EVA_CODE INNER JOIN sctrans.T_TS_FIN_ORG fin ON pri.FIN_ORG_ID = fin.PKID LEFT JOIN sctrans.T_TO_CASE cas ON pri.CASE_CODE = cas.CASE_CODE LEFT JOIN sctrans.T_TO_SIGN si ON pri.CASE_CODE= si.CASE_CODE LEFT JOIN sctrans.T_TO_WORKFLOW wf ON pri.EVA_CODE = wf.BIZ_CODE LEFT JOIN sctrans.ACT_RU_TASK ART ON wf.INST_CODE = ART.PROC_INST_ID_
pri.PKID = #{PKID,jdbcType=BIGINT}
AND pri.EVA_CODE = #{evaCode,jdbcType=VARCHAR}

mapper.update:
UPDATE 
	sctrans.T_TO_EGU_PROPERTY_INFO
SET 
	HOUSE_AGE = #{houseAge,jdbcType=INTEGER}
where 
	EVA_CODE = #{evaCode,jdbcType=VARCHAR}

UPDATE sctrans.T_TO_EVA_PRICING
SET STATUS = '1', COMPLETE_TIME = CURRENT_TIMESTAMP
SET STATUS = '2', REASON = #{reason,jdbcType=VARCHAR}
WHERE PKID = #{pkid,jdbcType=BIGINT}
