mapper.select:
select * from sctrans.T_TS_FIN_ORG where fin_org_code in( select tfo.fa_fin_org_code from sctrans.T_TS_FIN_ORG tfo inner join sctrans.T_TS_BANK_EVA_RELATIONSHIP tbe on tfo.fin_org_code=tbe.bank_code and tbe.EVA_COMPANY_CODE=#{evaCompanyCode,jdbcType=VARCHAR}
inner join sctrans.T_TS_SUP ts on ts.fin_org_code=tfo.fin_org_code and (ts.tags like '%'+#{tag,jdbcType=VARCHAR}+'%')
)

select * from sctrans.T_TS_FIN_ORG where fin_org_code in (select distinct FA_FIN_ORG_CODE from sctrans.T_TS_FIN_ORG where FIN_ORG_CODE in(select FIN_ORG_CODE from sctrans.T_TS_SUP ts where ts.SUP_CAT='0'
and (ts.tags like '%'+#{tag,jdbcType=VARCHAR}+'%' )
))

select ts.*,tt.CO_LEVEL from sctrans.T_TS_FIN_ORG ts right join sctrans.T_TS_SUP as tt on tt.fin_org_code= ts.fin_org_code and tt.sup_cat='0' where fa_fin_org_code=#{faFinOrgCode,jdbcType=VARCHAR}
and (tt.tags like '%'+#{tag,jdbcType=VARCHAR}+'%')
order by tt.CO_LEVEL asc
