mapper.select:
select kpi_Orders from sctrans.T_TS_EPLUS_PERFORM_MONTH 
where PARTICIPANT_NAME = #{participantName,jdbcType=VARCHAR}
and job_Id = #{jobId,jdbcType=VARCHAR}
and org_Id = #{orgId,jdbcType=VARCHAR}
and DATEDIFF(m, belong_Month, #{belongMonth,jdbcType=TIMESTAMP})=-1

mapper.delete:
delete
from sctrans.T_TS_EPLUS_PERFORM_MONTH
where PARTICIPANT_NAME = #{participantName,jdbcType=VARCHAR}
and job_Id = #{jobId,jdbcType=VARCHAR}
and org_Id = #{orgId,jdbcType=VARCHAR}
and belong_Month = #{belongMonth,jdbcType=TIMESTAMP}
