mapper.select:
    SELECT  v.value FROM sctrans.SYS_PARAM_DEFINE d
    LEFT JOIN sctrans.SYS_PARAM_VALUE v ON d.id = v.DEF_ID
	WHERE   d.CODE = 'SIGN_ROOM_OPEN_DATE';

    	SELECT sr.NUMBE_OF_ACCOMMODATE_PEOPLE as numberOfPeople,COUNT(*) AS residualNumber FROM sctrans.T_RM_SIGN_ROOM sr
		LEFT JOIN sctrans.T_RM_ROOM_SCHEDULE rs ON rs.ROOM_ID = sr.PKID
		WHERE rs.IS_DELETE = 0 AND rs.USE_STATUS = 'N' AND rs.START_DATE = #{startTime,jdbcType=VARCHAR} AND rs.END_DATE = #{endTime,jdbcType=VARCHAR} AND sr.ORG_ID = #{orgId,jdbcType=VARCHAR}
		GROUP BY sr.NUMBE_OF_ACCOMMODATE_PEOPLE;
