mapper.select:
select 

from sctrans.T_RM_SIGN_ROOM

  	 
    ROOM_TYPE = #{roomType,jdbcType=VARCHAR}
  
  
    AND ROOM_NO = #{roomNo,jdbcType=VARCHAR}
  
  
    AND TRADE_CENTER = #{tradeCenter,jdbcType=NVARCHAR}
  
  
    AND TRADE_CENTER_ID = #{tradeCenterId,jdbcType=CHAR}
  
  
    AND NUMBE_OF_ACCOMMODATE_PEOPLE = #{numbeOfAccommodatePeople,jdbcType=INTEGER}
  
  
    AND REMARK = #{remark,jdbcType=NVARCHAR}
  
  
    AND CLOSE_TIME = #{closeTime,jdbcType=TIMESTAMP}
  
  
    AND ROOM_STATUS = #{roomStatus,jdbcType=VARCHAR}
  
  
    AND IS_DELETE = #{isDelete,jdbcType=INTEGER}
  
  
    AND CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}
  
  
    AND CREATE_BY=#{createBy,jdbcType=VARCHAR}
  
  
    AND UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}
  
  
    AND UPDATE_BY = #{updateBy,jdbcType=VARCHAR}

select count(1) from sctrans.T_RM_SIGN_ROOM where IS_DELETE = 0
AND ROOM_NO = #{roomNo,jdbcType=VARCHAR} COLLATE Chinese_PRC_CS_AI
AND TRADE_CENTER_ID = #{tradeCenterId,jdbcType=CHAR}

SELECT DISTINCT R.PKID, R.ROOM_NO,R.ROOM_TYPE,R.TRADE_CENTER,R.NUMBE_OF_ACCOMMODATE_PEOPLE FROM sctrans.T_RM_SIGN_ROOM R INNER JOIN sctrans.T_RM_ROOM_SCHEDULE T ON R.PKID = T.ROOM_ID LEFT JOIN sctrans.T_RM_RESERVATION S ON T.RES_ID = S.PKID AND S.IS_DELETE = 0 WHERE T.IS_DELETE = '0' AND R.IS_DELETE = '0'
AND DATEDIFF(DAY, T.START_DATE, #{curDate}) = 0
AND R.ROOM_TYPE = #{roomType}
AND RTrim(T.USE_STATUS) = 'N'
AND S.RES_STATUS = #{useStatus,jdbcType=VARCHAR}
order by R.ROOM_NO

SELECT T.PKID, T.ROOM_TYPE, T.ROOM_NO, T.TRADE_CENTER, T.TRADE_CENTER_ID, T.NUMBE_OF_ACCOMMODATE_PEOPLE, T.REMARK, T.CLOSE_TIME, T.ROOM_STATUS , T.IS_DELETE, T.CREATE_TIME , T.CREATE_BY , T.UPDATE_TIME, T.UPDATE_BY , R.PKID AS stragegyPkid, R.STRAGEGY_WEEK_VAL AS stragegyWeekVal FROM [sctrans].[T_RM_SIGN_ROOM] T INNER JOIN [sctrans].T_RM_ROOM_SCHE_STRAGEGY R ON T.PKID = R.ROOM_ID where T.IS_DELETE = 0
AND T.ROOM_TYPE = #{roomType}
AND T.ROOM_STATUS = #{roomStatus}
