mapper.select:
SELECT DISTINCT R.PKID, R.ROOM_NO,R.ROOM_TYPE,R.ORG_NAME,R.NUMBE_OF_ACCOMMODATE_PEOPLE FROM sctrans.T_RM_SIGN_ROOM R INNER JOIN sctrans.T_RM_ROOM_SCHEDULE T ON R.PKID = T.ROOM_ID LEFT JOIN sctrans.T_RM_RESERVATION S ON T.RES_ID = S.PKID WHERE T.IS_DELETE = '0' AND R.IS_DELETE = '0'
AND DATEDIFF(DAY, T.START_DATE, #{curDate}) = 0
AND R.ORG_ID = #{orgId}
AND R.ROOM_TYPE = #{roomType}
AND RTrim(T.USE_STATUS) = 'N'
AND S.RES_STATUS = #{useStatus,jdbcType=VARCHAR}
order by R.ROOM_NO

SELECT T.PKID, T.ROOM_TYPE, T.ROOM_NO, T.TRADE_CENTER, T.TRADE_CENTER_ID, T.ORG_ID, T.ORG_NAME, T.NUMBE_OF_ACCOMMODATE_PEOPLE, T.REMARK, T.CLOSE_TIME, T.ROOM_STATUS , T.IS_DELETE, T.CREATE_TIME , T.CREATE_BY , T.UPDATE_TIME, T.UPDATE_BY , R.PKID AS stragegyPkid, R.STRAGEGY_WEEK_VAL AS stragegyWeekVal FROM [sctrans].[T_RM_SIGN_ROOM] T INNER JOIN [sctrans].T_RM_ROOM_SCHE_STRAGEGY R ON T.PKID = R.ROOM_ID
T.ORG_ID = #{orgId}
AND T.ROOM_TYPE = #{roomType}
AND T.ROOM_STATUS = #{roomStatus}
