mapper.select:
select
*
from sctrans.T_TS_TEAM_SCOPE_TARGET targetScope

	
		AND targetScope.GRP_CODE = #{grpCode}
	
	
		AND targetScope.YU_TEAM_CODE = #{yuTeamCode}


	select		
		*
	from sctrans.T_TS_TEAM_SCOPE_TARGET targetScope	
	WHERE	1=1
	AND   targetScope.GRP_CODE = #{grpCode}
	AND   targetScope.IS_RESPONSE_TEAM = #{isResponseTeam}
