select count(*) from ( SELECT min(l.color) as color FROM [sctrans].[T_TO_TRANS_PLAN] as t left join sctrans.T_TO_PROPERTY_INFO as tp on t.case_code=tp.case_code
left join sctrans.t_TO_workflow as w on w.case_code=t.CASE_CODE left join sctrans.act_ru_task ta on ta.[TASK_DEF_KEY_]=t.PART_CODE and w.INST_CODE=ta.[PROC_INST_ID_] and ta.suspension_state_='1' left join sctrans.LAMP_RULE l on GETDATE()-t.EST_PART_TIME>=l.DELAYTIME left join sctrans.SYS_USER as u on ta.ASSIGNEE_=u.USERNAME left join sctrans.v_user_org_job as uoj on uoj.[USER_ID]=u.ID where u.id is not null
and u.ID=#{userId,jdbcType=DECIMAL}
and uoj.ORG_ID=#{orgId,jdbcType=VARCHAR}
and uoj.ORG_ID in #{org1}
group by t.pkid ) as a where a.color=#{color}

  SELECT  SUM(APPLY_AMOUNT) as loanAmount
FROM    ( SELECT    SUM(ISNULL(la.loan_amount, 0)) AS APPLY_AMOUNT
          FROM      sctrans.T_TO_LOAN_AGENT la
          WHERE  la.CASE_CODE is not null and la.CASE_CODE <>'' and la.APPLY_TIME is not null    
          
	 	    and la.EXECUTOR_ID=#{userId,jdbcType=DECIMAL}
	     
	     
	 	    and la.executor_team=#{orgId,jdbcType=VARCHAR}
	     
	     
	 	    and la.executor_team in
	 	    #{org1}
	     
	     
	  	     and DATEDIFF(YYYY, la.APPLY_TIME, GETDATE())=0 and DatePart(m, la.APPLY_TIME)=#{mo,jdbcType=VARCHAR}
	     
          UNION ALL
          SELECT    SUM(ISNULL(tec.APPLY_AMOUNT, 0) * 10000) AS APPLY_AMOUNT
          FROM      sctrans.T_TO_ELOAN_CASE tec
          WHERE    tec.CASE_CODE is not null and tec.CASE_CODE <>'' and tec.APPLY_TIME is not null 
          
	 	    and tec.EXCUTOR_ID=#{userId,jdbcType=DECIMAL}
	     
	     
	 	    and tec.EXCUTOR_TEAM=#{orgId,jdbcType=VARCHAR}
	     
	     
	 	    and tec.excutor_team in
	 	    #{org1}
	     
	     
	  	     and DATEDIFF(YYYY, tec.APPLY_TIME, GETDATE())=0 and DatePart(m, tec.APPLY_TIME)=#{mo,jdbcType=VARCHAR}
	     
        ) A

  	SELECT  SUM(SIGN_AMOUNT) / SUM(CON_PRICE) as  tansferRate
FROM    ( SELECT    SUM(ISNULL(la.SIGN_AMOUNT, 0)) AS SIGN_AMOUNT ,
                    SUM(tsi.CON_PRICE) AS CON_PRICE
          FROM      sctrans.T_TO_LOAN_AGENT la
                    LEFT JOIN sctrans.t_to_sign tsi ON la.CASE_CODE = tsi.CASE_CODE
          WHERE
             la.CASE_CODE IS NOT NULL
             AND la.CASE_CODE <> ''
             AND la.APPLY_TIME IS NOT NULL
          	
	 	and la.EXECUTOR_ID=#{userId,jdbcType=DECIMAL}
	 
	 
	 	and la.executor_team=#{orgId,jdbcType=VARCHAR}
	 
	 
	 	and la.executor_team in
	 	#{org1}
	 
	  
	  	and DATEDIFF(YYYY, la.sign_time, GETDATE())=0 and DatePart(m, la.sign_time)=#{mo,jdbcType=VARCHAR}
	  
          UNION ALL
          SELECT    SUM(ISNULL(tec.SIGN_AMOUNT, 0) * 10000) AS SIGN_AMOUNT ,
                    SUM(tsi.CON_PRICE) AS CON_PRICE
          FROM      sctrans.T_TO_ELOAN_CASE tec
                    LEFT JOIN sctrans.t_to_sign tsi ON tec.CASE_CODE = tsi.CASE_CODE
          WHERE     tec.CASE_CODE IS NOT NULL
                    AND tec.CASE_CODE <> ''
                    AND tec.APPLY_TIME IS NOT NULL
          
	 	and tec.EXCUTOR_ID=#{userId,jdbcType=DECIMAL}
	 
	 
	 	and tec.excutor_team=#{orgId,jdbcType=VARCHAR}
	 
	 
	 	and tec.excutor_team in
	 	#{org1}
	 
	  
	  	and DATEDIFF(YYYY, tec.sign_time, GETDATE())=0 and DatePart(m, tec.sign_time)=#{mo,jdbcType=VARCHAR}
	  
        ) A

  		SELECT  SUM(SIGN_AMOUNT) as  signAmount
FROM    ( SELECT    SUM(ISNULL(la.SIGN_AMOUNT, 0)) AS SIGN_AMOUNT
          FROM      sctrans.T_TO_LOAN_AGENT la
          WHERE  la.CASE_CODE is not null and la.CASE_CODE <>'' and la.APPLY_TIME is not null    
          
	 			and la.EXECUTOR_ID=#{userId,jdbcType=DECIMAL}
	 		
	 		
	 			and la.executor_team=#{orgId,jdbcType=VARCHAR}
			 
			 
	 			and la.executor_team in
	 		#{org1}
	 	
	  
	  	and DATEDIFF(YYYY, la.sign_time, GETDATE())=0 and DatePart(m, la.sign_time)=#{mo,jdbcType=VARCHAR}
	  
          UNION ALL
          SELECT    SUM(ISNULL(tec.SIGN_AMOUNT,0)*10000) AS SIGN_AMOUNT
          FROM      sctrans.T_TO_ELOAN_CASE tec
          WHERE    tec.CASE_CODE is not null and tec.CASE_CODE <>'' and tec.APPLY_TIME is not null 
          
	 			and tec.EXCUTOR_ID=#{userId,jdbcType=DECIMAL}
	 		
	 		
	 			and tec.excutor_team=#{orgId,jdbcType=VARCHAR}
			 
			 
	 			and tec.excutor_team in
	 		#{org1}
	 	
	  
	  	and DATEDIFF(YYYY, tec.sign_time, GETDATE())=0 and DatePart(m, tec.sign_time)=#{mo,jdbcType=VARCHAR}
	  
        ) A

  	SELECT SUM(ACTUAL_AMOUNT) AS actualAmount,
(sum(SIGN_AMOUNT)/(case when SUM(REAL_PRICE)=0 THEN  0.001 else SUM(REAL_PRICE)  end)*100) as convRate
FROM 
(SELECT  SUM(la.ACTUAL_AMOUNT) AS ACTUAL_AMOUNT ,
        SUM(la.SIGN_AMOUNT) AS SIGN_AMOUNT ,
        SUM(tsi.REAL_PRICE) AS REAL_PRICE
FROM    sctrans.T_TO_LOAN_AGENT la
        LEFT JOIN sctrans.t_to_sign tsi ON la.CASE_CODE = tsi.CASE_CODE
WHERE   la.CASE_CODE is not null and la.CASE_CODE <>'' 
		
	 	and la.EXECUTOR_ID=#{userId,jdbcType=DECIMAL}
	 
	 
	 	and la.executor_team=#{orgId,jdbcType=VARCHAR}
	 
	 
	 	and la.executor_team in
	 	#{org1}
	 
	  
	  	and DATEDIFF(YYYY, la.RELEASE_TIME, GETDATE())=0 and DatePart(m, la.RELEASE_TIME)=#{mo,jdbcType=VARCHAR}
	  
UNION ALL
SELECT  NULL AS ACTUAL_AMOUNT ,
        SUM(ttc.SIGN_AMOUNT) AS SIGN_AMOUNT ,
        SUM(tsi.REAL_PRICE) AS REAL_PRICE
FROM    sctrans.T_TO_ELOAN_CASE ttc
        LEFT JOIN sctrans.t_to_sign tsi ON ttc.CASE_CODE = tsi.CASE_CODE
WHERE   ttc.CASE_CODE is not null and ttc.CASE_CODE <>'' 
		
	 	and ttc.EXCUTOR_ID=#{userId,jdbcType=DECIMAL}
	 
	 
	 	and ttc.excutor_team=#{orgId,jdbcType=VARCHAR}
	 
	 
	 	and ttc.excutor_team in
	 	#{org1}
	 
	  
	  	and DATEDIFF(YYYY, ttc.SIGN_TIME, GETDATE())=0 and DatePart(m, ttc.SIGN_TIME)=#{mo,jdbcType=VARCHAR}
	  
UNION ALL
SELECT  SUM(ttr.RELEASE_AMOUNT * 10000) AS ACTUAL_AMOUNT ,
        NULL AS SIGN_AMOUNT ,
        NULL AS REAL_PRICE
FROM    sctrans.T_TO_ELOAN_REL ttr
        LEFT JOIN sctrans.T_TO_ELOAN_CASE ttc ON ttc.ELOAN_CODE = ttr.ELOAN_CODE
WHERE   ttc.CASE_CODE is not null and ttc.CASE_CODE <>'' 
		
	 	and ttc.EXCUTOR_ID=#{userId,jdbcType=DECIMAL}
	 
	 
	 	and ttc.excutor_team=#{orgId,jdbcType=VARCHAR}
	 
	 
	 	and ttc.excutor_team in
	 	#{org1}
	 
	  
	  	and DATEDIFF(YYYY, ttr.RELEASE_TIME, GETDATE())=0 and DatePart(m, ttr.RELEASE_TIME)=#{mo,jdbcType=VARCHAR}
	  
        ) A

SELECT COUNT(a.CASE_CODE) AS countQYS FROM sctrans.V_RPT_CASE_PROCESS_STATIS a INNER JOIN sctrans.V_CASE_LIST_BASE b ON a.CASE_CODE=b.CASE_CODE WHERE a.RECEIVED_TIME IS NOT NULL
AND #{userId,jdbcType=DECIMAL} = a.RECEIVED_USER
AND a.TEAM_ID=#{orgId,jdbcType=VARCHAR}
AND a.TEAM_ID IN #{org1}
AND DatePart(m, a.RECEIVED_TIME)=#{mo,jdbcType=VARCHAR}

SELECT COUNT(wf.CASE_CODE) FROM( SELECT TASK_DEF_KEY_ AS TASK_DEF_KEY, PROC_INST_ID_ AS PROC_INST_ID, NAME_ AS NAME, ASSIGNEE_ AS ASSIGNEE, START_TIME_ AS START_TIME, END_TIME_ AS END_TIME, DELETE_REASON_ AS DELETE_REASON, (SELECT TOP 1 USER_ID FROM sctrans.V_USER_ORG_JOB WHERE USERNAME=assignee_ AND (JOB_CODE ='consultant' OR JOB_CODE = 'Manager' OR JOB_CODE='director' OR JOB_CODE = 'GeneralManager')) AS consultant, (SELECT TOP 1 ORG_ID FROM sctrans.V_USER_ORG_JOB WHERE USERNAME=assignee_ AND JOB_CODE='consultant') AS ORG FROM sctrans.ACT_HI_TASKINST )historyTask INNER JOIN sctrans.T_TO_workflow wf ON historyTask.PROC_INST_ID=wf.INST_CODE INNER JOIN sctrans.T_TO_CASE_INFO ci ON wf.CASE_CODE=ci.CASE_CODE LEFT JOIN sctrans.T_TO_CASE as tc ON tc.case_code=ci.case_code INNER JOIN sctrans.T_TO_PROPERTY_INFO AS property ON ci.CASE_CODE = property.CASE_CODE LEFT JOIN SCTRANS.SYS_USER SYSUSER ON SYSUSER.ID = ci.AGENT_CODE WHERE historyTask.END_TIME IS NOT NULL AND historytask.DELETE_REASON = 'completed' AND historyTask.TASK_DEF_KEY='TransSign'
AND #{userId,jdbcType=DECIMAL} = historyTask.consultant
AND historyTask.ORG =#{orgId,jdbcType=VARCHAR}
AND historyTask.ORG IN #{org1}
AND DATEPART(m,historyTask.END_TIME)=#{mo,jdbcType=VARCHAR}

SELECT COUNT(distinct a.CASE_CODE) FROM sctrans.V_RPT_CASE_PROCESS_STATIS a WHERE a.HOUSE_TRANFER_TIME is not null
AND #{userId,jdbcType=DECIMAL} = a.HOUSE_TRANFER_USER
AND a.HOUSE_TRANFER_TEAM_ID=#{orgId,jdbcType=VARCHAR}
AND a.HOUSE_TRANFER_TEAM_ID IN #{org1}
and DatePart(m, a.HOUSE_TRANFER_TIME)=#{mo,jdbcType=VARCHAR}

SELECT COUNT(wf.CASE_CODE) FROM( SELECT TASK_DEF_KEY_ AS TASK_DEF_KEY, PROC_INST_ID_ AS PROC_INST_ID, NAME_ AS NAME, ASSIGNEE_ AS ASSIGNEE, START_TIME_ AS START_TIME, END_TIME_ AS END_TIME, DELETE_REASON_ AS DELETE_REASON, (SELECT TOP 1 USER_ID FROM sctrans.V_USER_ORG_JOB WHERE USERNAME=assignee_ AND (JOB_CODE ='consultant' OR JOB_CODE = 'Manager' OR JOB_CODE='director' OR JOB_CODE = 'GeneralManager')) AS consultant, (SELECT TOP 1 ORG_ID FROM sctrans.V_USER_ORG_JOB WHERE USERNAME=assignee_ AND JOB_CODE='consultant') AS ORG FROM sctrans.ACT_HI_TASKINST )historyTask INNER JOIN sctrans.T_TO_workflow wf ON historyTask.PROC_INST_ID=wf.INST_CODE INNER JOIN sctrans.T_TO_CASE_INFO ci ON wf.CASE_CODE=ci.CASE_CODE LEFT JOIN sctrans.T_TO_CASE as tc ON tc.case_code=ci.case_code INNER JOIN sctrans.T_TO_PROPERTY_INFO AS property ON ci.CASE_CODE = property.CASE_CODE LEFT JOIN SCTRANS.SYS_USER SYSUSER ON SYSUSER.ID = ci.AGENT_CODE WHERE historyTask.END_TIME IS NOT NULL AND historytask.DELETE_REASON = 'completed' AND historyTask.TASK_DEF_KEY='ComLoanProcess'
AND #{userId,jdbcType=DECIMAL} = historyTask.consultant
AND historyTask.ORG =#{orgId,jdbcType=VARCHAR}
AND historyTask.ORG IN #{org1}
AND DATEPART(m,historyTask.END_TIME)=#{mo,jdbcType=VARCHAR}

SELECT COUNT(wf.CASE_CODE) FROM( SELECT TASK_DEF_KEY_ AS TASK_DEF_KEY, PROC_INST_ID_ AS PROC_INST_ID, NAME_ AS NAME, ASSIGNEE_ AS ASSIGNEE, START_TIME_ AS START_TIME, END_TIME_ AS END_TIME, DELETE_REASON_ AS DELETE_REASON, (SELECT TOP 1 USER_ID FROM sctrans.V_USER_ORG_JOB WHERE USERNAME=assignee_ AND (JOB_CODE ='consultant' OR JOB_CODE = 'Manager' OR JOB_CODE='director' OR JOB_CODE = 'GeneralManager')) AS consultant, (SELECT TOP 1 ORG_ID FROM sctrans.V_USER_ORG_JOB WHERE USERNAME=assignee_ AND JOB_CODE='consultant') AS ORG FROM sctrans.ACT_HI_TASKINST )historyTask INNER JOIN sctrans.T_TO_workflow wf ON historyTask.PROC_INST_ID=wf.INST_CODE INNER JOIN sctrans.T_TO_CASE_INFO ci ON wf.CASE_CODE=ci.CASE_CODE LEFT JOIN sctrans.T_TO_CASE as tc ON tc.case_code=ci.case_code INNER JOIN sctrans.T_TO_PROPERTY_INFO AS property ON ci.CASE_CODE = property.CASE_CODE LEFT JOIN SCTRANS.SYS_USER SYSUSER ON SYSUSER.ID = ci.AGENT_CODE WHERE historyTask.END_TIME IS NOT NULL AND historytask.DELETE_REASON = 'completed' AND historyTask.TASK_DEF_KEY='CaseClose'
AND #{userId,jdbcType=DECIMAL} = historyTask.consultant
AND historyTask.ORG =#{orgId,jdbcType=VARCHAR}
AND historyTask.ORG IN #{org1}
AND DATEPART(m,historyTask.END_TIME)=#{mo,jdbcType=VARCHAR}

select sum(tf.EVAL_FEE) as evalFee, (sum(tf.EVAL_FEE)/ (case when sum(ts.REAL_PRICE/1000)=0 then 0.001 else sum(ts.REAL_PRICE/1000) end) *100)as efConvRate from sctrans.T_TO_EVA_FEE_RECORD as tf left join sctrans.T_TG_SERV_ITEM_AND_PROCESSOR as tsi on tf.CASE_CODE=tsi.CASE_CODE and tsi.SRV_CODE='3000400101' left join sctrans.t_to_sign as ts on tf.CASE_CODE=ts.CASE_CODE where tf.CASE_CODE is not null and tf.RECORD_TIME is not null
and tsi.PROCESSOR_ID=#{userId,jdbcType=DECIMAL}
and tsi.ORG_ID =#{orgId,jdbcType=VARCHAR}
and tsi.org_id in #{org1}
and DATEDIFF(YYYY, tf.RECORD_TIME, GETDATE())=0 and DatePart(m, tf.RECORD_TIME)=#{mo,jdbcType=VARCHAR}

  	SELECT 
		CAST(( COUNT(tf.CASE_CODE) )/( CASE WHEN COUNT(tm.CASE_CODE) = 0 THEN 0.001 ELSE COUNT(tm.CASE_CODE) END ) * 100 AS DECIMAL(10, 2)) AS efCount
	FROM    sctrans.T_TG_SERV_ITEM_AND_PROCESSOR AS tsi
        INNER JOIN sctrans.T_TO_CASE tc ON tc.CASE_CODE = tsi.CASE_CODE
        LEFT JOIN sctrans.T_TO_EVA_FEE_RECORD AS tf ON tsi.CASE_CODE = tf.CASE_CODE
                 
					and DATEDIFF(YYYY, tf.RECORD_TIME, GETDATE())=0 and DatePart(m, tf.RECORD_TIME)=#{mo,jdbcType=VARCHAR}
				
        LEFT JOIN sctrans.T_TO_MORTGAGE tm ON tm.CASE_CODE = tsi.CASE_CODE
                                              AND tm.APPR_DATE IS NOT NULL
                                              AND tm.IS_ACTIVE = '1'
                
					and DATEDIFF(YYYY, tm.APPR_DATE, GETDATE())=0 and DatePart(m, tm.APPR_DATE)=#{mo,jdbcType=VARCHAR}
				
	WHERE
		tsi.SRV_CODE='3000400101'
	 
	 	and tsi.PROCESSOR_ID=#{userId,jdbcType=DECIMAL}
	 
	 
	 	and tsi.ORG_ID =#{orgId,jdbcType=VARCHAR}
	 
	 
	 	and tsi.org_id in
	 	#{org1}

select rank_no from sctrans.T_TS_DAYLY_RANK where RANK_CAT=#{rankCat,jdbcType=VARCHAR} and RANK_TYPE=#{rankType,jdbcType=VARCHAR} and RANK_ID=#{userId,jdbcType=VARCHAR}
AND belong_org_id=#{orgId,jdbcType=VARCHAR}
and DATEDIFF(day, RANK_DAY, GETDATE())=0 and RANK_DURATION='MONTH'

select top 5 * from sctrans.T_TS_DAYLY_RANK where RANK_CAT=#{rankCat,jdbcType=VARCHAR} and RANK_TYPE=#{rankType,jdbcType=VARCHAR}
AND belong_org_id=#{orgId,jdbcType=VARCHAR}
AND belong_org_id IN #{org1}
and DATEDIFF(day, RANK_DAY, GETDATE())=0 and RANK_DURATION=#{rankDuration,jdbcType=VARCHAR} order by RANK_NO asc

select la.LOAN_SRV_CODE staItem,SUM(la.loan_amount) as [amount],count(la.loan_amount) as [count], CAST((sum(la.loan_amount)/(case when SUM(tsi.REAL_PRICE)=0 then 0.001 end)*100) as decimal(38, 2)) as convRate from sctrans.T_TO_LOAN_AGENT as la left join sctrans.t_to_sign as tsi on la.CASE_CODE=tsi.CASE_CODE where la.CASE_CODE is not null and la.CASE_CODE <> '' and la.apply_time is not null and la.loan_amount is not null
and la.EXECUTOR_ID=#{userId,jdbcType=DECIMAL}
and la.executor_team=#{orgId,jdbcType=VARCHAR}
and la.executor_team in #{org1}
and DATEDIFF(YYYY, la.APPLY_TIME, GETDATE())=0 and DatePart(m, la.APPLY_TIME)=#{mo,jdbcType=VARCHAR}
group by la.LOAN_SRV_CODE

select la.LOAN_SRV_CODE as staItem,SUM(la.sign_amount) as [amount],count(la.sign_amount) as [count],
CAST((sum(la.sign_amount)/(case when SUM(tsi.REAL_PRICE)=0 then 0.001 else SUM(tsi.REAL_PRICE) end)*100) as decimal(38, 2)) as convRate from sctrans.T_TO_LOAN_AGENT as la left join sctrans.t_to_sign as tsi on la.CASE_CODE=tsi.CASE_CODE where la.CASE_CODE is not null and la.CASE_CODE <>'' and la.sign_time is not null and la.sign_amount is not null
and la.EXECUTOR_ID=#{userId,jdbcType=DECIMAL}
and la.executor_team=#{orgId,jdbcType=VARCHAR}
and la.executor_team in #{org1}
and DATEDIFF(YYYY, la.sign_time, GETDATE())=0 and DatePart(m, la.sign_time)=#{mo,jdbcType=VARCHAR}
group by la.LOAN_SRV_CODE

select la.LOAN_SRV_CODE as staItem,SUM(la.actual_amount) as [amount],count(la.actual_amount) as [count],
CAST((sum(la.actual_amount)/(case when SUM(tsi.REAL_PRICE) =0 then 0.001 else SUM(tsi.REAL_PRICE) end)*100) as decimal(38, 2)) as convRate from sctrans.T_TO_LOAN_AGENT as la left join sctrans.t_to_sign as tsi on la.CASE_CODE=tsi.CASE_CODE where la.CASE_CODE is not null and la.CASE_CODE <>'' and la.release_time is not null and la.actual_amount is not null
and la.EXECUTOR_ID=#{userId,jdbcType=DECIMAL}
and la.executor_team=#{orgId,jdbcType=VARCHAR}
and la.executor_team in #{org1}
and DATEDIFF(YYYY, la.release_time, GETDATE())=0 and DatePart(m, la.release_time)=#{mo,jdbcType=VARCHAR}
group by la.LOAN_SRV_CODE
