select * from ( SELECT B.PKID, B.STATUS AS STATUS_OLD, B.RED_COUNT, B.PROPERTY_ADDR, B.CASE_CODE, B.AGENT_NAME, B.GRP_NAME AGENT_ORG_NAME, ( select stuff((select ',' +a.GUEST_NAME from sctrans.T_TG_GUEST_INFO a where a.CASE_CODE=B.CASE_CODE and a.TRANS_POSITION='30006001' for xml path('')),1,1,'')) sname, B.CTM_CODE as ctmCode,B.AGENT_PHONE, B.AGENT_CODE,us.EMPLOYEE_CODE, (SELECT U.REAL_NAME FROM sctrans.SYS_USER U inner join sctrans.T_TO_CASE C on U.ID=C.LEADING_PROCESS_ID AND C.CASE_CODE = B.CASE_CODE) FONT_NAME
FROM sctrans.V_CASE_LIST_BASE B left join sctrans.SYS_USER us on B.AGENT_CODE=us.ID where ( EXISTS ( SELECT 1 FROM SCTRANS.T_TO_CASE C WHERE C.CASE_CODE = B.CASE_CODE ) OR EXISTS ( SELECT 1 FROM SCTRANS.T_TG_SERV_ITEM_AND_PROCESSOR C WHERE C.CASE_CODE = B.CASE_CODE ) OR EXISTS ( SELECT 1 FROM SCTRANS.T_TG_SERV_ITEM_AND_PROCESSOR A INNER JOIN ( SELECT T.*,S.id FROM SCTRANS.T_TL_TASK_REASSIGNT_LOG T LEFT JOIN sctrans.SYS_USER S ON t.ORI_ASSIGNEE = S.username ) L ON L.id = A.PROCESSOR_ID and L.case_code = A.case_code <ifNotNull userLoginName> AND L.NEW_ASSIGNEE = #userLoginName#</ifNotNull> WHERE B.CASE_CODE = A.CASE_CODE AND A.SRV_CODE IN ( '3000400201', '3000401002' ) ) )
) c where 1=1
