SELECT CASE WHEN GROUPING(j.ORG_NAME) = 1 THEN '合计' ELSE j.ORG_NAME end as orgName, sum((CASE WHEN loanlost_apply_reason LIKE '%收入、流水不足%' THEN 1 ELSE 0 END)) reason1 ,sum((CASE WHEN loanlost_apply_reason LIKE '%客户资质差、征信有问题%' THEN 1 ELSE 0 END)) reason2 ,sum((CASE WHEN loanlost_apply_reason LIKE '%客户亲戚、朋友在银行上班%' THEN 1 ELSE 0 END)) reason3 ,sum((CASE WHEN loanlost_apply_reason LIKE '%客户是银行VIP%' THEN 1 ELSE 0 END)) reason4 ,sum((CASE WHEN loanlost_apply_reason LIKE '%客户自己找的银行优惠折扣大%' THEN 1 ELSE 0 END)) reason5 ,sum((CASE WHEN loanlost_apply_reason LIKE '%客户不愿意支付评估费%' THEN 1 ELSE 0 END)) reason6 ,sum((CASE WHEN loanlost_apply_reason LIKE '%房龄老、面积小等不予受理案件%' THEN 1 ELSE 0 END)) reason9 ,sum((CASE WHEN loanlost_apply_reason LIKE '%银行退单，客户自办%' THEN 1 ELSE 0 END)) reason10 ,sum((CASE WHEN loanlost_apply_reason LIKE '%中原无法办理案件%' THEN 1 ELSE 0 END)) reason11 ,sum((CASE WHEN loanlost_apply_reason LIKE '%客户坚持自己办理%' THEN 1 ELSE 0 END)) reason7 ,sum((CASE WHEN loanlost_apply_reason LIKE '%房东坚持客户到他指定银行办理%' THEN 1 ELSE 0 END)) reason8 ,sum((CASE WHEN loanlost_apply_reason LIKE '%分行原因导致案件流失%' THEN 1 ELSE 0 END)) reason12 ,sum((CASE WHEN loanlost_apply_reason IS NULL OR loanlost_apply_reason LIKE '%其他%' THEN 1 ELSE 0 END)) reason13 FROM sctrans.T_TO_MORTGAGE m left join sctrans.T_TO_CASE c on m.CASE_CODE=c.CASE_CODE left join sctrans.SYS_ORG j on c.DISTRICT_ID=j.ID WHERE (m.IS_DELEGATE_YUCUI =0 AND MORT_TYPE = '30016001') AND IS_ACTIVE = 1 AND EXISTS(SELECT a.CASE_CODE FROM sctrans.T_TO_APPROVE_RECORD a WHERE m.CASE_CODE = a.CASE_CODE AND CONTENT LIKE '通过%' AND PART_CODE LIKE 'GuohuApprove'
<ifNotNull serviceDepId> AND c.DISTRICT_ID=#serviceDepId# </ifNotNull> <ifNotNull startDate> AND a.OPERATOR_TIME &gt;=#startDate# </ifNotNull> <ifNotNull endDate> AND a.OPERATOR_TIME &lt;#endDate# </ifNotNull>
)

SELECT CASE WHEN GROUPING(j.ORG_NAME) = 1 THEN '合计' ELSE j.ORG_NAME end as orgName, sum((CASE WHEN loanlost_apply_reason LIKE '%收入、流水不足%' THEN 1 ELSE 0 END)) reason1 ,sum((CASE WHEN loanlost_apply_reason LIKE '%客户资质差、征信有问题%' THEN 1 ELSE 0 END)) reason2 ,sum((CASE WHEN loanlost_apply_reason LIKE '%客户亲戚、朋友在银行上班%' THEN 1 ELSE 0 END)) reason3 ,sum((CASE WHEN loanlost_apply_reason LIKE '%客户是银行VIP%' THEN 1 ELSE 0 END)) reason4 ,sum((CASE WHEN loanlost_apply_reason LIKE '%客户自己找的银行优惠折扣大%' THEN 1 ELSE 0 END)) reason5 ,sum((CASE WHEN loanlost_apply_reason LIKE '%客户不愿意支付评估费%' THEN 1 ELSE 0 END)) reason6 ,sum((CASE WHEN loanlost_apply_reason LIKE '%房龄老、面积小等不予受理案件%' THEN 1 ELSE 0 END)) reason9 ,sum((CASE WHEN loanlost_apply_reason LIKE '%银行退单，客户自办%' THEN 1 ELSE 0 END)) reason10 ,sum((CASE WHEN loanlost_apply_reason LIKE '%中原无法办理案件%' THEN 1 ELSE 0 END)) reason11 ,sum((CASE WHEN loanlost_apply_reason LIKE '%客户坚持自己办理%' THEN 1 ELSE 0 END)) reason7 ,sum((CASE WHEN loanlost_apply_reason LIKE '%房东坚持客户到他指定银行办理%' THEN 1 ELSE 0 END)) reason8 ,sum((CASE WHEN loanlost_apply_reason LIKE '%分行原因导致案件流失%' THEN 1 ELSE 0 END)) reason12 ,sum((CASE WHEN loanlost_apply_reason IS NULL OR loanlost_apply_reason LIKE '%其他%' THEN 1 ELSE 0 END)) reason13 FROM sctrans.T_TO_MORTGAGE m left join sctrans.T_TO_CASE c on m.CASE_CODE=c.CASE_CODE left join sctrans.SYS_ORG j on c.ORG_ID=j.ID WHERE (m.IS_DELEGATE_YUCUI =0 AND MORT_TYPE = '30016001') AND IS_ACTIVE = 1 AND EXISTS(SELECT a.CASE_CODE FROM sctrans.T_TO_APPROVE_RECORD a WHERE m.CASE_CODE = a.CASE_CODE AND CONTENT LIKE '通过%' AND PART_CODE LIKE 'GuohuApprove'
<ifNotNull serviceDepId> AND c.ORG_ID=#serviceDepId# </ifNotNull> <ifNotNull startDate> AND a.OPERATOR_TIME &gt;=#startDate# </ifNotNull> <ifNotNull endDate> AND a.OPERATOR_TIME &lt;#endDate# </ifNotNull>
)
