query.searchScript:
SELECT
er.PKID,
er.ELOAN_CODE,
ec.LOAN_SRV_CODE,
er.RELEASE_AMOUNT,
er.RELEASE_TIME,
er.CONFIRM_STATUS,
INFO.PROPERTY_ADDR,
ec.CUST_NAME,
ec.FIN_ORG_CODE,
ec.EXCUTOR_ID,
ec.EXCUTOR_TEAM
FROM
sctrans.T_TO_ELOAN_REL er
LEFT JOIN sctrans.T_TO_ELOAN_CASE ec on
ER.ELOAN_CODE=ec.ELOAN_CODE
LEFT JOIN sctrans.T_TO_PROPERTY_INFO INFO
ON INFO.CASE_CODE =
ec.CASE_CODE
LEFT JOIN sctrans.sys_user u ON
ec.EXCUTOR_ID=u.ID

	(ec.STATUS IS NULL OR ec.STATUS='VALID')

		 and
			ec.EXCUTOR_ID= #{sessionUserId} and ec.EXCUTOR_TEAM
			=#{serviceDepId}

		 and (ec.EXCUTOR_TEAM
			=#{serviceDepId} )

		 and ec.EXCUTOR_TEAM=#{serviceDepId}

		 and ec.EXCUTOR_DISTRICT
			=#{serviceDepId}


			and er.ELOAN_CODE=#{eloanCode}


			and ec.LOAN_SRV_CODE=#{loanSrvCode}


			and er.CONFIRM_STATUS=#{status}


			and  ec.EXCUTOR_TEAM=#{team}


			and ec.EXCUTOR_ID=#{excutorId}


			and INFO.PROPERTY_ADDR=#{address}


			and er.RELEASE_TIME > =#{startTime}


			and er.RELEASE_TIME < =#{endTime}


			and ec.FIN_ORG_CODE =#{finOrgCode}

                SELECT 
                sum(case when c.LOAN_SRV_CODE='30004015' or  c.LOAN_SRV_CODE='30004005' then 1 else 0 end) as ka, 
                sum(case when c.LOAN_SRV_CODE!='30004015' or  c.LOAN_SRV_CODE!='30004005' then 1 else 0 end) as dai ,
                sum(case when c.LOAN_SRV_CODE='30004015' or  c.LOAN_SRV_CODE='30004005' then r.RELEASE_AMOUNT else 0 end) as kaAmount, 
                sum(case when c.LOAN_SRV_CODE!='30004015' or  c.LOAN_SRV_CODE!='30004005' then r.RELEASE_AMOUNT else 0 end) as daiAmount ,
		        DatePart(mm, r.RELEASE_TIME) as mm,DatePart(yyyy, r.RELEASE_TIME) as  years
                from sctrans.T_TO_ELOAN_REL r
                LEFT JOIN sctrans.T_TO_ELOAN_CASE c ON r.ELOAN_CODE=c.ELOAN_CODE 
                LEFT JOIN sctrans.T_TO_PROPERTY_INFO INFO ON INFO.CASE_CODE =c.CASE_CODE

				(c.STATUS IS NULL OR c.STATUS='VALID')

					 and
						c.EXCUTOR_ID= #{sessionUserId} and c.EXCUTOR_TEAM
						=#{serviceDepId}

					 and (c.EXCUTOR_TEAM
						=#{serviceDepId} )

					 and c.EXCUTOR_TEAM=#{serviceDepId}

					 and c.EXCUTOR_DISTRICT
						=#{serviceDepId}


						and r.ELOAN_CODE=#{eloanCode}


						and c.LOAN_SRV_CODE=#{loanSrvCode}


						and r.CONFIRM_STATUS=#{status}


						and  c.EXCUTOR_TEAM=#{team}


						and c.EXCUTOR_ID=#{excutorId}


						and INFO.PROPERTY_ADDR=#{address}


						and r.RELEASE_TIME > =#{startTime}


						and r.RELEASE_TIME < = #{endTime}


						and c.FIN_ORG_CODE =#{finOrgCode}



			  GROUP BY DatePart(mm, r.RELEASE_TIME),DatePart(yyyy, r.RELEASE_TIME)

                SELECT count(c.FIN_ORG_CODE ) as num, c.FIN_ORG_CODE as finOrgName ,sum(r.RELEASE_AMOUNT) RELEASE_AMOUNT from sctrans.T_TO_ELOAN_REL r
                LEFT JOIN sctrans.T_TO_ELOAN_CASE c ON r.ELOAN_CODE=c.ELOAN_CODE 
                LEFT JOIN sctrans.T_TO_PROPERTY_INFO INFO ON INFO.CASE_CODE =c.CASE_CODE

				(c.STATUS IS NULL OR c.STATUS='VALID') 

					 and
						c.EXCUTOR_ID= #{sessionUserId} and c.EXCUTOR_TEAM
						=#{serviceDepId}

					 and (c.EXCUTOR_TEAM
						=#{serviceDepId} )

					 and c.EXCUTOR_TEAM=#{serviceDepId}

					 and c.EXCUTOR_DISTRICT
						=#{serviceDepId}


						and r.ELOAN_CODE=#{eloanCode}


						and c.LOAN_SRV_CODE=#{loanSrvCode}


						and r.CONFIRM_STATUS=#{status}


						and  c.EXCUTOR_TEAM=#{team}


						and c.EXCUTOR_ID=#{excutorId}


						and INFO.PROPERTY_ADDR=#{address}


						and r.RELEASE_TIME > =#{startTime}


						and r.RELEASE_TIME < = #{endTime}


						and c.FIN_ORG_CODE =#{finOrgCode}



			 GROUP BY c.FIN_ORG_CODE
			 ORDER BY  c.FIN_ORG_CODE
