SELECT 1 as type, ec.PKID, er.ELOAN_CODE as loanCode, ec.LOAN_SRV_CODE, er.RELEASE_AMOUNT as releaseAmout, er.RELEASE_TIME, er.CONFIRM_STATUS, INFO.PROPERTY_ADDR, ec.CUST_NAME, ec.FIN_ORG_CODE, ec.EXCUTOR_ID as ecutorId, ec.EXCUTOR_TEAM as ecutorTeam FROM sctrans.T_TO_ELOAN_REL er LEFT JOIN sctrans.T_TO_ELOAN_CASE ec on ER.ELOAN_CODE=ec.ELOAN_CODE LEFT JOIN sctrans.T_TO_PROPERTY_INFO INFO ON INFO.CASE_CODE = ec.CASE_CODE
(ec.STATUS IS NULL OR ec.STATUS='VALID')
and ec.EXCUTOR_ID= #{sessionUserId} and ec.EXCUTOR_TEAM =#{serviceDepId}
and (ec.EXCUTOR_TEAM =#{serviceDepId} )
and ec.EXCUTOR_TEAM=#{serviceDepId}
and ec.EXCUTOR_DISTRICT =#{serviceDepId}
and er.ELOAN_CODE=#{eloanCode}
and ec.LOAN_SRV_CODE=#{loanSrvCode}
and er.CONFIRM_STATUS=#{status}
and ec.EXCUTOR_TEAM=#{teamCode}
and ec.EXCUTOR_ID=#{excutorId}
and INFO.PROPERTY_ADDR=#{address}
and er.RELEASE_TIME > =#{startTime}
and er.RELEASE_TIME < =#{endTime}
and ec.FIN_ORG_CODE =#{finOrgCode}
UNION SELECT 2 as type, loanAgent.PKID, '' as loanCode, loanAgent.LOAN_SRV_CODE, loanAgent.ACTUAL_AMOUNT/10000 as releaseAmout, loanAgent.RELEASE_TIME, loanAgent.CONFIRM_STATUS, INFO.PROPERTY_ADDR, loanAgent.CUST_NAME, loanAgent.FIN_ORG_CODE, loanAgent.EXECUTOR_ID as ecutorId, loanAgent.EXECUTOR_TEAM as ecutorTeam FROM sctrans.T_TO_LOAN_AGENT loanAgent
LEFT JOIN sctrans.T_TO_PROPERTY_INFO INFO ON INFO.CASE_CODE = loanAgent.CASE_CODE LEFT JOIN sctrans.sys_org org ON loanAgent.EXECUTOR_TEAM=org.ID
loanAgent.CONFIRM_STATUS='3'
and loanAgent.EXECUTOR_ID= #{sessionUserId} and loanAgent.EXECUTOR_TEAM =#{serviceDepId}
and (loanAgent.EXECUTOR_TEAM =#{serviceDepId} )
and loanAgent.EXECUTOR_TEAM=#{serviceDepId}
and org.PARENT_ID =#{serviceDepId}
and loanAgent.LOAN_SRV_CODE=#{loanSrvCode}
and loanAgent.EXECUTOR_TEAM=#{teamCode}
and loanAgent.EXECUTOR_ID=#{excutorId}
and INFO.PROPERTY_ADDR=#{address}
and loanAgent.RELEASE_TIME > =#{startTime}
and loanAgent.RELEASE_TIME < =#{endTime}
and loanAgent.FIN_ORG_CODE =#{finOrgCode}

select  * from( SELECT 
